use crate::utils;
use std::{collections::HashMap, time::Instant};

fn split_if_even(num: usize) -> Option<(usize, usize)> {
    let s = num.to_string();
    if s.len() % 2 == 0 {
        let str = s.as_str();
        Some((
            str[..s.len() / 2].parse().unwrap(),
            str[s.len() / 2..].parse().unwrap(),
        ))
    } else {
        None
    }
}

// Stub generated by Elf
pub fn solve() {
    // elf splits the printed output at the first <space> and takes writes the first half as the solution
    let input = utils::file_to_string("2024", "11")
        .split_whitespace()
        .filter_map(|n| n.parse().ok())
        .collect::<Vec<usize>>();
    let mut time = Instant::now();
    let p1 = part_one(&input);
    println!("{:?} in {:?} for Part 1", p1, time.elapsed());
    time = Instant::now();
    let p2 = part_two(&input);
    println!("{:?} in {:?} for Part 2", p2, time.elapsed());
}

fn part_one(stones: &[usize]) -> usize {
    blink(25, stones)

}
fn part_two(stones: &[usize]) -> usize {
    blink(75, stones)
}

fn blink(count: usize, initial: &[usize]) -> usize {
    let mut number_counts: HashMap<usize, usize> = HashMap::new();
    initial.iter().for_each(|&stone| {
        number_counts
            .entry(stone)
            .and_modify(|v| *v += 1)
            .or_insert(1);
    });

    for _ in 0..count {
        let mut tmp: HashMap<usize, usize> = HashMap::new();
        for (k, v) in &number_counts {
            if *k == 0 {
                tmp.entry(1)
                    .and_modify(|ones| *ones += *v)
                    .or_insert(*v);
            } else if let Some((left, right)) = split_if_even(*k) {
                tmp.entry(left)
                    .and_modify(|val| *val += *v)
                    .or_insert(*v);
                tmp.entry(right)
                    .and_modify(|val| *val += *v)
                    .or_insert(*v);
            } else {
                tmp.entry(k * 2024)
                    .and_modify(|val| *val += *v)
                    .or_insert(*v);
            }
        }
        number_counts = tmp;
    }
    number_counts.values().sum()
}
